css:
@media print {
    @page {
        size: 100mm 150mm;
        margin: 0;
    }
}

body {
    margin: 0;
    padding: 0;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 11px;
}

.shipping-label {
    width: 100mm;
    height: 150mm;
    padding: 8mm;
    box-sizing: border-box;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 2px solid #000;
    padding-bottom: 5px;
    margin-bottom: 6px;
}

.logo {
    max-height: 26px;
    max-width: 80px;
    object-fit: contain;
}

.section {
    margin-bottom: 6px;
}

.section.dashed {
    border-top: 1px dashed #000;
    padding-top: 6px;
}

.two-col {
    display: flex;
    justify-content: space-between;
}

.right {
    text-align: right;
}

.row {
    display: flex;
    justify-content: space-between;
    margin-bottom: 3px;
    gap: 6px;
}

.row span:last-child {
    text-align: right;
    flex: 1;
}

.barcode-section {
    margin-top: 8px;
    display: flex;
    justify-content: space-between;
    gap: 4px;
}

.barcode {
    width: 32%;
    max-height: 45px;
    object-fit: contain;
}

.outsourced-label {
    width: 100%;
    max-height: 95mm;
    object-fit: contain;
}

.page-break {
    page-break-after: always;
}

html:
{% set from_idx = (frappe.form_dict.from_idx or 1) | int %}
{% set to_idx = (frappe.form_dict.to_idx or doc.orders | length) | int %}

{% for order in doc.orders if order.idx >= from_idx and order.idx <= to_idx %}

{% if order.delivery_method == "In-House" %}
<div class="shipping-label">

    <!-- HEADER: LOGOS -->
    <div class="header">
        <img src="/files/miraaya-logo.png" class="logo">
        <img src="/files/aramex-logo.jpeg" class="logo">
    </div>

    <!-- SHIPPER INFO -->
    {% set co_address = frappe.db.sql("""
        SELECT address_line1, address_line2, city, country
        FROM `tabAddress`
        WHERE is_your_company_address = 1
          AND address_type = 'Billing'
        LIMIT 1
    """, as_dict=1) %}
    
    {% if co_address %}
        {% set co_address = co_address[0] %}
    {% else %}
        {% set co_address = {"address_line1":"", "address_line2":"", "city":"", "country":""} %}
    {% endif %}
    <div class="section two-col">
        <div>
            <strong>Miraaya Co</strong><br>
            {{ co_address.address_line1 or "" }}<br>
            {{ co_address.address_line2 or "" }}<br>
            {{ co_address.city or "" }}, {{ co_address.country or "" }}
        </div>
        <div class="right">
            {{ frappe.format_date(frappe.utils.nowdate()) }}<br>
            {{ frappe.utils.nowtime() }}
        </div>
    </div>

    <!-- CUSTOMER DETAILS -->
    <div class="section dashed">
        <div class="row"><span>Order ID</span><span>{{ order.magento_id }}</span></div>
        <div class="row"><span>Customer</span><span>{{ order.customer_name }}</span></div>
        <div class="row"><span>Phone</span><span>{{ order.contact_no or "N/A" }}</span></div>
        <div class="row">
            <span>Address</span>
            <span>
                {{ [order.address, order.landmark, order.city] | select | join(", ") }}
            </span>
        </div>
        <div class="row"><span>Total</span>
            <span>{{ frappe.format_value(order.grand_total, {"fieldtype":"Currency"}) }}</span>
        </div>
        <div class="row"><span>Payment</span><span>{{ order.payment_method or "N/A" }}</span></div>
        <div class="row"><span>Delivery Date</span>
            <span>{{ frappe.format_date(order.delivery_date) if order.delivery_date }}</span>
        </div>
        <div class="row"><span>Delivery Time</span><span>{{ order.delivery_time or "" }}</span></div>
        <div class="row"><span>Items</span><span>{{ order.total_qty or "" }}</span></div>
    </div>

    <!-- BARCODES -->
    <div class="barcode-section">
        {% if order.magento_barcode %}
            <img src="{{ order.magento_barcode }}" class="barcode">
        {% endif %}
        {% if order.picklist_barcode %}
            <img src="{{ order.picklist_barcode }}" class="barcode">
        {% endif %}
        {% if order.order_barcode %}
            <img src="{{ order.order_barcode }}" class="barcode">
        {% endif %}
    </div>

</div>

{% elif order.delivery_method == "Outsourced" %}
<div class="shipping-label outsourced">

    <!-- OUTSOURCED LABEL -->
    {% if order.outsourced_label %}
        <img src="{{ order.outsourced_label }}" class="outsourced-label">
    {% endif %}

    <!-- QR CODES -->
    <div class="barcode-section">
        {% if order.magento_barcode %}
            <img src="{{ order.magento_barcode }}" class="barcode">
        {% endif %}
        {% if order.picklist_barcode %}
            <img src="{{ order.picklist_barcode }}" class="barcode">
        {% endif %}
    </div>

</div>
{% endif %}

{% if not loop.last %}
<div class="page-break"></div>
{% endif %}

{% endfor %}
