css:
@media print {
    @page {
        size: 4in 6in;
        margin: 0;
    }

    body {
        margin: 0;
        padding: 0;
    }
}

.shipping-label {
    width: 4in;
    height: 6in;
    padding: 10px;
    box-sizing: border-box;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 12px;
    line-height: 1.3;
    page-break-inside: avoid;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 2px solid #000;
    padding-bottom: 6px;
    margin-bottom: 8px;
}

.company-name {
    font-size: 20px;
    font-weight: bold;
    letter-spacing: 1px;
}

.section {
    margin-bottom: 10px;
}

.section.order-details {
    border-top: 1px dashed #000;
    padding-top: 6px;
}

.row {
    display: flex;
    justify-content: space-between;
    gap: 6px;
    margin-bottom: 4px;
}

.label {
    font-weight: bold;
    white-space: nowrap;
    border: none !important;
    outline: none !important;
    background: transparent !important;
}

.value {
    text-align: right;
    flex: 1;
}

.value {
    word-wrap: break-word;
    overflow-wrap: break-word;
}

.barcode-wrapper {
    margin-top: 10px;
    text-align: center;
}

.barcode {
    max-height: 45px;
}

.barcode-large {
    width: 100%;
    max-height: 90px;
}

.page-break-always {
    page-break-after: always;
}
